function isProdPage(){
	var ProdString = 'var PAGE_NAME = "ProductPage";';
	scripts = document.getElementsByTagName('script')
	for(var i=0; i<scripts.length ; i++){
		if (scripts[i].innerHTML.indexOf(ProdString) !== -1) {
			// console.log(scripts[i].innerHTML);
			return true
		}
	}	
}

function isSubArray (subArray, array) {
	for(var i = 0 , len = subArray.length; i < len; i++) {
		if(array.indexOf(subArray[i]) == -1) return false;
	}
	return true;
}

function findCityId (city) {
	for (var idx in cities){
		if (cities[idx].CityName == city.toLowerCase())
			return cities[idx].CityId;
	}
	return '27';
}

function getListContent() {
	result = document.getElementsByClassName('clp-breadcrumb');
	var txt = result[0].firstElementChild.innerText;
	return txt.replace(/\s+/g, "").split('>');
}

function mapCategory(category_candidates){
	for(var idx in category_candidates){
		var value = category_candidates[idx];
		// console.log()
		if (value.indexOf("Mobile") > -1) 
			return '149';
		else if (value.indexOf("Laptop") > -1)
			return '147' ;
		else if (value.indexOf("Book") > -1)
			return '52'  ;
		else if (value.indexOf("Camera") > -1)
			return '112' ;
	}
	return 'No results';
}

function getQuikrResults(category, city, product, from, size, recos, maxItr){
	var xhr = new XMLHttpRequest({mozSystem: true});
	xhr.open('get', 'https://api.quikr.com/public/adsByCategory?categoryId='+category+'&city='+city+'&from='+from+'&size='+size);
	xhr.setRequestHeader('Content-Type', 'application/json');
	xhr.setRequestHeader('X-Quikr-App-Id', '537');
	xhr.setRequestHeader('X-Quikr-Token-Id', '3035004');
	xhr.setRequestHeader('X-Quikr-Signature', '3268823f3172d7e970844d6d2415be47b945fd2f');
	xhr.send();

	xhr.onreadystatechange = function() {
		if (xhr.readyState == 4 && xhr.status == 200) {
			var resultjson = JSON.parse(xhr.responseText).AdsByCategoryResponse.AdsByCategoryData.docs;

	        // Match model number
	        for (var idx in resultjson) {
	        	var item = resultjson[idx];
	        	var modelNumber = product.replace((item.attribute_Brand_name+'').toLowerCase(), '');
	        	
	        	var condition = (item.attribute_Ad_Type == 'offer') ? 
	        	((item.attribute_Model+'').toLowerCase().indexOf(modelNumber) > -1) ||
	        	isSubArray(product.toLowerCase().split(' '), item.title.toLowerCase().split(' ')) : false;
	        	
	        	if (condition) {
	        		resultArray.push(item);
	        	}
	        }

	        // Add result to popup
	        // resultArray.sort(function (a,b){
	        // 	return parseInt(a.attribute_Price+'') - parseInt(b.attribute_Price+'');
	        // });
	        // (resultArray != null && resultArray.length>0) ? console.log(resultArray) : null;

	        if(resultArray.length < recos && maxItr > 1)
	        	getQuikrResults(category, city, product, from+size, size, recos-resultArray.length, --maxItr);

	    }
	    
	};
}

function displayLocation(latitude,longitude){
	var request = new XMLHttpRequest();

	var method = 'GET';
	var url = 'http://maps.googleapis.com/maps/api/geocode/json?latlng='+latitude+','+longitude+'&sensor=true';
	var async = true;

	request.open(method, url, async);
	request.onreadystatechange = function(){
		if(request.readyState == 4 && request.status == 200){
			var data = JSON.parse(request.responseText);
	        var addressComponents = data.results[0].address_components;
	        for(i=0;i<addressComponents.length;i++){
	        	var types = addressComponents[i].types
	           if(types=="locality,political"){
	              // alert(addressComponents[i].long_name);
	              startQuikrFind(addressComponents[i].long_name);
	          }
	      }
	   }
	};
	request.send();
};

var successCallback = function(position){
	var x = position.coords.latitude;
	var y = position.coords.longitude;
	displayLocation(x,y);
};


function startQuikrFind(city){
	if(isProdPage()) {
		var categoryId = mapCategory(getListContent());
		var cityId = findCityId(city);
		var product = document.getElementsByClassName('title-wrap line omniture-field')[0].firstElementChild.innerHTML.split('(')[0];

		// alert(categoryId +' - '+city+' - '+product);

		searchPage = 'http://' + city.toLowerCase() + '.quikr.com/' + product.split(' ').join('-') + '-all/'+ product.split(' ').join('-') + '/x' + cityId;
		resultArray.push({'link': searchPage});
		getQuikrResults(categoryId, city, product, 1, 100, 4, 10);
		// console.log(searchPage);
	}
}

navigator.geolocation.getCurrentPosition(successCallback);

